function myAxios(e){return new Promise((s,t)=>{const n=new XMLHttpRequest;if(e.params){const s=new URLSearchParams(e.params).toString();e.url+=`?${s}`}if(n.open(e.method||"GET",e.url),n.addEventListener("loadend",()=>{n.status>=200&&n.status<300?s(JSON.parse(n.response)):t(new Error(n.response))}),e.data){const s=JSON.stringify(e.data);n.setRequestHeader("Content-Type","application/json"),n.send(s)}else n.send()})}